import React, {
    Component
} from 'react';

import Answer from "./Answer";

class Question extends Component {

    render() {
        const {question, index} = this.props;
        const answers = Object.questions.question.answers.sort(() => 0.5 - Math.random());
        return (
            <div className= "question">
                <div className="form-group pt-2">
                    <label className="font-weight-semibold"><strong>{index + 1}. {question.cont}</strong></label>
                    {
                        answers.map(answer =>{
                            return <Answer key={answer.key} answer={answer}
                                            corrects={question.corrects}/>
                        })
                    }
                </div>
            </div>
        );
    }
}

export default Question;
